<div style="margin: 10%; margin-left: 30%">
  <h1 style="margin: 10%; margin-left: 30%" class="modal-title">
    Registration
  </h1>

  <div class="modal-body">
    <form (ngSubmit)="addRegister()" [formGroup]="registrationForm">
      <div class="form-group">
        <label>Username<span class="text-danger">*</span></label>
        <input
          class="form-control"
          type="text"
          placeholder="username"
          [class.invalid]="registrationForm.get('userName')"
          formControlName="userName"
        />
      </div>
      <div class="form-group">
        <label>Mobile<span class="text-danger">*</span></label>
        <input
          class="form-control"
          type="text"
          placeholder="mobile no."
          [class.invalid]="registrationForm.get('mobile')"
          formControlName="mobile"
        />
      </div>
      <div class="form-group">
        <label>Password<span class="text-danger">*</span></label>
        <input
          class="form-control"
          type="text"
          placeholder="password"
          [class.invalid]="registrationForm.get('password')"
          formControlName="password"
        />
      </div>
      <div class="submit-section">
        <button class="btn btn-primary submit-btn" type="submit">Submit</button>
      </div>
    </form>
    <h4>
      Already have an account ?
      <h4 routerLink="/login" class="text-danger">Login here</h4>
    </h4>
  </div>
</div>
