import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  public loginForm!: FormGroup<any>;

  constructor(
    private http: HttpClient,
    private formBuilder: FormBuilder,
    private router: Router
  ) {}
  ngOnInit() {
    this.loginForm = this.formBuilder.group({
      userName: ['', [Validators.required]],

      password: ['', [Validators.required]],
    });
  }
  userLogin() {
    let obj = {
      userName: this.loginForm.value.userName,
      password: this.loginForm.value.password,
    };

    this.http
      .post('http://localhost:8443/api/login/userLogin', obj)
      .subscribe((res: any) => {
        if (res) {
          sessionStorage.setItem('userName', res.userName);
          let al = sessionStorage.getItem('userName');
          sessionStorage.setItem('dataValue', '1');
        }
        console.log(res, 'kkk');
        window.location.reload();

        this.router.navigate(['audios']);
      });
  }
}
<div style="margin: 10%; margin-left: 30%">
  <h1 style="margin: 10%; margin-left: 30%" class="modal-title">Login</h1>
  <form [formGroup]="loginForm" (ngSubmit)="userLogin()">
    <div class="form-group">
      <label>Username<span class="text-danger">*</span></label>
      <input
        class="form-control"
        type="text"
        placeholder="username"
        formControlName="userName"
      />
    </div>
    <div class="form-group">
      <label>Password<span class="text-danger">*</span></label>
      <input
        class="form-control"
        type="password"
        placeholder="password"
        formControlName="password"
      />
    </div>
    <button
      style="margin: 3%; margin-left: 40%"
      class="btn btn-primary submit-btn"
      type="submit"
    >
      Submit
    </button>
  </form>
  <h4>
    Don't have an account ?
    <span
      routerLink="/registration"
      class="text-primary"
      style="cursor: pointer"
    >
      Create new account
    </span>
  </h4>
</div>
